def handleBrackets(result, char):
    if(char is "("):
        result+=1
    elif(char is ")"):
        result-=1

    return result

def isInBasement(result):
    if(result is -1):
        return True

    return False

def main():
    result = 0
    index = 0
    global inputFile

    for char in inputFile:
        result = handleBrackets(result, char)

        if(isInBasement(result)):
            print("result is: " + str(index + 1))
            break

        index+=1


if  __name__ =='__main__':main()
